    setStars(starString);
    setCount(count + 1); // countÎ•º 1 Ï¶ùÍ∞Ä
  }


  return (
    <div>
      <button onClick={drawStars}>Draw Stars</button>
      <pre>{stars}</pre>
      <p>Click Count: {count}</p>
    </div>
  );
}

export default StarGenerator;
