import React, { useState } from 'react';

const StarGenerator = () => {
  const [count, setCount] = useState(0);
  const [stars, setStars] = useState('');

  const drawStars = () => {
    let starString = '';
    // for (let i = 0; i < count; i++) {
    //   starString += ' '.repeat(i) + '*'.repeat(count - i) + '\n'; // Î≥ÑÏ∞çÍ∏∞ Î°úÏßÅ
    // }
    for (let i = 0; i <= count; i++) {
      for (let j = 0; j <= i; j++) {
        starString += ' ';
      }
      for (let k = 0; k < count - i; k++) {
        starString += '*';
      }
      starString += '\n';
    }
    setStars(starString);
    setCount(count + 1); // Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú countÎ•º 1 Ï¶ùÍ∞Ä
  }

  return (
    <div>
      <button onClick={drawStars}>Draw Stars</button>
      <pre>{stars}</pre>
      <p>Click Count: {count}</p>
    </div>
  );
}

export default StarGenerator;
