import React, { useState } from 'react';

const App = () => {
  const [count, setCount] = useState(0);
  const [starsSection1, setStarsSection1] = useState('');
  const [starsSection2, setStarsSection2] = useState('');
  const [starsSection3, setStarsSection3] = useState('');
  const [starsSection4, setStarsSection4] = useState('');

  const drawStars = () => {
    let starString1 = '';
    let starString2 = '';
    let starString3 = '';
    let starString4 = '';

    // Section 1: Regular pattern
    for (let i = 0; i <= count; i++) {
      for (let j = 0; j <= i; j++) {
        starString1 += '*';
      }
      starString1 += '\n';
    }

    // Section 2: Inverted regular pattern
    for (let i = count; i >= 0; i--) {
      for (let j = 0; j <= i; j++) {
        starString2 += '*';
      }
      starString2 += '\n';
    }

    // Section 3: Alternating stars and spaces
    for (let i = count; i >= 0; i--) {
      for (let j = 0; j <= i; j++) {
        starString2 += '*';
      }
      starString2 += '\n';
    }

    // Section 4: Pyramid pattern
    for (let i = 0; i <= count; i++) {
      for (let j = 0; j < count - i; j++) {
        starString4 += ' ';
      }
      for (let k = 0; k <= i * 2; k++) {
        starString4 += '*';
      }
      starString4 += '\n';
    }

    setStarsSection1(starString1);
    setStarsSection2(starString2);
    setStarsSection3(starString3);
    setStarsSection4(starString4);

    setCount(count + 1); // Increment count
  };

  return (
    <div>
      <button onClick={drawStars}>{count}+</button>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1, marginRight: '10px' }}>
          <pre>{starsSection1}</pre>
          <pre>{starsSection2}</pre>
        </div>
        <div style={{ flex: 1 }}>
          <pre>{starsSection3}</pre>
          <pre>{starsSection4}</pre>
        </div>
      </div>
    </div>
  );
};

export default App;
