import React, { useState } from 'react';

const StarGenerator = () => {
  const [count, setCount] = useState(0);
  const [stars, setStars] = useState('');

  const handleClick = () => {
    let starString = '';
    for (let i = 0; i < count; i++) {
      starString += '★';
    }
    setStars(starString);
  }

  return (
    <div>
      <button onClick={handleClick}>Generate Stars</button>
      <div>{stars}</div>
      <p>Stars Count: {count}</p>
    </div>
  );
}

export default StarGenerator;
